fidin=fopen('radar_out.txt');targets=[];i_ans=0;while ~feof(fidin)    tline=fgetl(fidin);    i_ans=i_ans+1;    for i=1:str2num(tline())        tline1=fgetl(fidin);        temp_target=str2num(tline1);        found=false;        for target_id = 1:size(targets,2)            n= size(targets(target_id).sequence,2);            target_dis= targets(target_id).sequence(n).distance;            if (abs(temp_target(2)-target_dis)<10 && abs(cosd(temp_target(3))-cosd( targets(target_id).sequence(n).angle))<0.1 )                target_num= size(targets(target_id).sequence,2)+1;                targets(target_id).sequence(target_num).distance = temp_target(2);                targets(target_id).sequence(target_num).angle = temp_target(3);                targets(target_id).sequence(target_num).course = temp_target(6);                targets(target_id).sequence(target_num).speed = temp_target(7);                found=true;                break;                            end        end        if (found==false )            target_id= size(targets,2)+1;            targets(target_id).sequence(1).distance=temp_target(2);            targets(target_id).sequence(1).angle = temp_target(3);            targets(target_id).sequence(1).course = temp_target(6);            targets(target_id).sequence(1).speed = temp_target(7);                  end    endendshow_id=3;for(i=1:size(targets(show_id).sequence,2))    temp_dist(i)=targets(show_id).sequence(i).distance;    temp_angle(i)=(targets(show_id).sequence(i).angle);    temp_course(i)=(targets(show_id).sequence(i).course);    temp_speed(i)=targets(show_id).sequence(i).speed;endplot(temp_dist);hold on;yyaxis rightplot(temp_angle)valid_num=0;for (i=1:size(targets,2))    if (size(targets(i).sequence,2)>20)         valid_num=valid_num+1;    endend% show_id=3;% num=1;% for(i=1:size(radar_targets(show_id).sequence,2))%     if cosd(radar_targets(show_id).sequence(i).angle)>0.9%         temp_radardist(nu